val nycData = spark.read
    .format("csv")
    .option("header", "true")
    .load("/data/nyc-jobs.csv")


// register a temp view that can be used for Spark SQL
nycData.createOrReplaceTempView("nycData")

// show the schema
nycData.printSchema

// show the number of records read
println(s"(${nycData.count} rows read)")

// remove rows that have null values
val completeData = nycData.na.drop
completeData.count
